var http = require('http');
var express = require('express');
var router = express.Router();
var bodyParser = require('body-parser');
var cookieParser = require('cookie-parser');
var cookieSession = require('cookie-session');
var log = require('bunyan').createLogger({name: 'system'});

/**
 * Start the server
 *
 * @param  {Object}     config          Configuration to start the server with
 * @param  {Function}   callback        Standard callback function
 * @param  {Object}     callback.err    Error object
 */
module.exports.initServer = function(config, callback) {
    log.info('Starting server');
    var app = module.exports.app = express();
    app.use(cookieParser());
    app.use(bodyParser());
    app.use(cookieSession({'secret': '1234567890'}));
    app.use(router);
    app.use(express.static(config.ui.path));

    http.createServer(app).listen(3000);
	callback(null, app);
};
